SET userName = ?, name = ?, address = ?, phoneNum = ?, email = ?, password = ?
WHERE adminId = ?;


UPDATE user
SET userName = ?, firstName = ?, lastName = ?, gender = ?, address = ?, phoneNo = ?, email = ?, password = ?
WHERE userId = ?;


